<?phpclass Block_BlkRandom extends Zend_View_Helper_Abstract {    function blkRandom($numOfRandom = 10) {        $cache = Zend_Registry::get('cache');        $gameModel = new Front_Model_Games();        if (!$listRandomGame = $cache->load('cache_random_game')) {            $listRandomGame = $gameModel->fetchRandom($numOfRandom);            $listRandomGame = $listRandomGame->toArray();            $cache->save($listRandomGame, 'cache_random_game');        }        require_once (BLOCK_PATH . '/BlkRandom/' . TEMPLATE_USED . '/default.php');    }}?>