<?phpclass Block_BlkRandomGame extends Zend_View_Helper_Abstract {    function blkRandomGame($numOfRandom = 12) {        $gameModel = new Front_Model_Games();        $listRandomGame = $gameModel->fetchRandom($numOfRandom);        $listRandomGame = $listRandomGame->toArray();        require_once (BLOCK_PATH . '/BlkRandomGame/' . TEMPLATE_USED . '/default.php');    }}?>